// src/services/api.js
import axios from "axios";

const API_URL = "http://localhost:3000/api";

export const fetchEvents = async (isArchived) => {
  try {
    const response = await axios.get(
      `${API_URL}/events${isArchived ? "archived" : ""}`
    );
    return response.data;
  } catch (error) {
    console.error("Etkinlikler alınamadı:", error);
    throw error;
  }
};

export const fetchArchivedEvents = async (date) => {
  try {
    const response = await axios.get(`${API_URL}/events/date?`);
    return response.data;
  } catch (error) {
    console.error("Etkinlikler alınamadı:", error);
    throw error;
  }
};

export const fetchEventById = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/events/${id}`);
    return response.data;
  } catch (error) {
    console.error("Etkinlik alınamadı:", error);
    throw error;
  }
};

export async function fetchCities() {
  try {
    const response = await axios.get(`${API_URL}/cities`);
    return response.data; // API'den gelen şehir verilerini döndür
  } catch (error) {
    throw new Error("Şehirler alınamadı: " + error.message);
  }
}

// ... Diğer API fonksiyonları
