// src/services/api.js
import axios from "axios";

const API_URL = "http://localhost:3000/api";

export const fetchEvents = async () => {
  try {
    const response = await axios.get(`${API_URL}/events`);
    return response.data;
  } catch (error) {
    console.error("Etkinlikler alınamadı:", error);
    throw error;
  }
};

