import React, { useState, useEffect } from "react";
import { fetchEvents } from "../services/api";
import {
  Container,
  Row,
  Col,
  Form,
  Button,
  Card,
  FormControl,
} from "react-bootstrap";

function HomePage() {
  const [events, setEvents] = useState([]);
  const [filter, setFilter] = useState({
    date: "",
    type: "",
    place: "",
    city: "",
  });

  useEffect(() => {
    loadEvents();
  }, []);

  const loadEvents = async () => {
    try {
      const eventData = await fetchEvents(filter);
      setEvents(eventData);
    } catch (error) {
      console.error("Etkinlikler alınamadı:", error);
    }
  };

  const handleFilterChange = (e) => {
    setFilter({
      ...filter,
      [e.target.name]: e.target.value,
    });
  };

  const applyFilters = () => {
    loadEvents();
  };

  const handleSearch = (e) => {
    const searchValue = e.target.value.toLowerCase();
    const filteredEvents = events.filter(
      (event) =>
        event.place.toLowerCase().includes(searchValue) ||
        event.city.toLowerCase().includes(searchValue)
    );
    setEvents(filteredEvents);
  };

  return (
    <Container>
      <h1 className="mt-4">Etkinlik Listesi</h1>
      <Row className="mb-4">
        <Col md={3}>
          <Form.Control
            type="date"
            name="date"
            value={filter.date}
            onChange={handleFilterChange}
            placeholder="Tarih"
          />
        </Col>
        <Col md={3}>
          <Form.Control
            type="text"
            name="type"
            value={filter.type}
            onChange={handleFilterChange}
            placeholder="Etkinlik Türü"
          />
        </Col>
        <Col md={3}>
          <Form.Control
            type="text"
            name="place"
            value={filter.place}
            onChange={handleFilterChange}
            placeholder="Mekan"
          />
        </Col>
        <Col md={3}>
          <Form.Control
            type="text"
            name="city"
            value={filter.city}
            onChange={handleFilterChange}
            placeholder="Şehir"
          />
        </Col>
        <Col md={3}>
          <Button variant="primary" onClick={applyFilters}>
            Filtrele
          </Button>
        </Col>
      </Row>
      <Row>
        <Col md={6}>
          <FormControl
            type="text"
            placeholder="Mekan veya Şehir Ara"
            onChange={handleSearch}
          />
        </Col>
      </Row>
      <Row>
        {events.map((event) => (
          <Col key={event.id} md={4} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{event.name}</Card.Title>
                <Card.Text>{event.description}</Card.Text>
                <Card.Text>Mekan: {event.place}</Card.Text>
                <Card.Text>Şehir: {event.city}</Card.Text>
                {/* Diğer etkinlik detayları... */}
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default HomePage;
