// src/pages/HomePage.js
import React, { useState, useEffect } from "react";
import { fetchEvents } from "../services/api";
import "bootstrap/dist/css/bootstrap.min.css"; // Bootstrap stil dosyasını projeye dahil edin

function HomePage() {
  const [events, setEvents] = useState([]);
  const [filter, setFilter] = useState({
    date: "",
    type: "",
    place: "",
    city: "",
  });

  useEffect(() => {
    loadEvents();
  }, []);

  const loadEvents = async () => {
    try {
      const eventData = await fetchEvents(filter);
      setEvents(eventData);
    } catch (error) {
      console.error("Etkinlikler alınamadı:", error);
    }
  };

  const handleFilterChange = (e) => {
    setFilter({
      ...filter,
      [e.target.name]: e.target.value,
    });
  };

  const applyFilters = () => {
    loadEvents();
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-4">
          <div className="form-group">
            <input
              type="date"
              className="form-control"
              name="date"
              value={filter.date}
              onChange={handleFilterChange}
              placeholder="Tarih"
            />
          </div>
        </div>
        <div className="col-md-4">
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              name="type"
              value={filter.type}
              onChange={handleFilterChange}
              placeholder="Etkinlik Türü"
            />
          </div>
        </div>
        <div className="col-md-4">
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              name="place"
              value={filter.place}
              onChange={handleFilterChange}
              placeholder="Mekan"
            />
          </div>
        </div>
        <div className="col-md-4">
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              name="city"
              value={filter.city}
              onChange={handleFilterChange}
              placeholder="Şehir"
            />
          </div>
        </div>
        <div className="col-md-4">
          <div className="form-group">
            <button className="btn btn-primary" onClick={applyFilters}>
              Filtrele
            </button>
          </div>
        </div>
      </div>
      <div className="row">
        {events.map((event) => (
          <div key={event.id} className="col-md-4">
            <div className="card mb-4">
              <div className="card-body">
                <h5 className="card-title">{event.name}</h5>
                <p className="card-text">{event.description}</p>
                {/* Diğer etkinlik detayları... */}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
