import React, { useEffect, useState } from "react";
import { Container, Row, Col, Carousel, Table } from "react-bootstrap";
import { fetchEventById } from "../services/api"; // API servisinin yolunu projenize göre ayarlayın

function EventDetailPage({ match }) {
  const [event, setEvent] = useState(null);
  const eventId = match.params.eventId;

  useEffect(() => {
    // Belirli etkinliği API'den çekmek için fetchEventById işlevini kullan
    fetchEventById(eventId)
      .then((data) => {
        setEvent(data);
      })
      .catch((error) => {
        console.error("Etkinlik alınamadı:", error);
      });
  }, [eventId]);

  if (!event) {
    return <div>Loading...</div>;
  }

  // Standları gruplayacak bir işlev
  const groupStandsByGroup = (stands) => {
    const groupedStands = {};
    stands.forEach((stand) => {
      const group = stand.group;
      if (!groupedStands[group]) {
        groupedStands[group] = [];
      }
      groupedStands[group].push(stand);
    });
    return groupedStands;
  };

  // Gruplanmış standları al
  const groupedStands = groupStandsByGroup(event.Stands);

  return (
    <Container>
      <Row>
        <Col>
          <Carousel>
            {event.Photos.length > 0 ? (
              event.Photos.map((photo) => (
                <Carousel.Item key={photo.id}>
                  <img
                    className="d-block w-100"
                    src={`http://localhost:3000/api/uploads/${photo.name}`}
                    alt={photo.name}
                  />
                </Carousel.Item>
              ))
            ) : (
              <img
                className="d-block w-100"
                src={`http://localhost:3000/api/uploads/sample.jpg`}
                alt="sample"
              />
            )}
          </Carousel>
        </Col>
      </Row>
      <Row>
        <Col>
          <h1>{event.name}</h1>
          <p>{event.description}</p>
          <p>Kategori: {event.categoryId}</p>
          <p>Adres: {event.address}</p>
          <p>Konum: {event.location}</p>
          <p>Başlangıç Tarihi: {new Date(event.startedAt).toLocaleString()}</p>
          <p>Bitiş Tarihi: {new Date(event.finishedAt).toLocaleString()}</p>
        </Col>
      </Row>
      <Row>
        <Col>
          <h2>Standlar</h2>
          {Object.keys(groupedStands).map((group) => (
            <div key={group}>
              <h3>{group}</h3>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Koltuk Numarası</th>
                    <th>Fiyat</th>
                  </tr>
                </thead>
                <tbody>
                  {groupedStands[group].map((stand) => (
                    <tr key={stand.id}>
                      <td>{stand.seatNumber}</td>
                      <td>{stand.price} TL</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </div>
          ))}
        </Col>
      </Row>
    </Container>
  );
}

export default EventDetailPage;
