import React, { useState, useEffect } from "react";
import { fetchEvents, fetchCities } from "../services/api"; // API servislerini projenizin yapısına göre ayarlayın
import { Link } from "react-router-dom";
import {
  Container,
  Row,
  Col,
  Form,
  Button,
  Card,
  Dropdown,
  DropdownButton,
} from "react-bootstrap";

function HomePage() {
  const [events, setEvents] = useState([]);
  const [filter, setFilter] = useState({
    date: "",
    type: "",
    place: "",
    city: "",
  });
  const [cities, setCities] = useState([]); // Şehirleri saklamak için bir dizi

  useEffect(() => {
    loadEvents();
    loadCities(); // Şehirleri yükle
  }, []);

  const loadEvents = async () => {
    try {
      const eventData = await fetchEvents(filter); // API servisi ile etkinlik verilerini çekin
      setEvents(eventData);
    } catch (error) {
      console.error("Etkinlikler alınamadı:", error);
    }
  };

  const loadCities = async () => {
    try {
      const cityData = await fetchCities(); // API servisi veya veritabanından şehir verilerini çekin
      setCities(cityData);
    } catch (error) {
      console.error("Şehirler alınamadı:", error);
    }
  };

  const handleFilterChange = (e) => {
    setFilter({
      ...filter,
      [e.target.name]: e.target.value,
    });
  };

  const applyFilters = () => {
    loadEvents();
  };

  return (
    <Container>
      <h1 className="mt-4">Etkinlik Listesi</h1>
      <Row className="mb-4">
        <Col md={3}>
          <Form.Control
            type="date"
            name="date"
            value={filter.date}
            onChange={handleFilterChange}
            placeholder="Tarih"
          />
        </Col>
        <Col md={2}>
          <Form.Control
            type="text"
            name="type"
            value={filter.type}
            onChange={handleFilterChange}
            placeholder="Etkinlik Türü"
          />
        </Col>
        <Col md={2}>
          <Form.Control
            type="text"
            name="place"
            value={filter.place}
            onChange={handleFilterChange}
            placeholder="Mekan"
          />
        </Col>
        <Col md={2}>
          <DropdownButton
            id="city-dropdown"
            title={filter.city ? filter.city : "Şehir Seç"}
          >
            {cities.map((city) => (
              <Dropdown.Item
                key={city.id}
                onClick={() => setFilter({ ...filter, city: city.name })}
              >
                {city.name}
              </Dropdown.Item>
            ))}
          </DropdownButton>
        </Col>
        <Col md={1}>
          <Button variant="primary" onClick={applyFilters}>
            Filtrele
          </Button>
        </Col>
      </Row>
      <Row>
        {events.map((event) => (
          <Col key={event.id} md={12} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{event.name}</Card.Title>
                <Card.Text>{event.description}</Card.Text>
                <Card.Text>Mekan: {event.place}</Card.Text>
                <Card.Text>Şehir: {event.city}</Card.Text>
                {/* Detaylara Git butonu */}
                <Link to={`/events/${event.id}`} className="btn btn-primary">
                  Detaylara Git
                </Link>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default HomePage;
