import { Container, Row, Col, Carousel } from "react-bootstrap";

// EventDetailPage.js

import React, { useEffect, useState } from "react";

import { fetchEventById } from "../services/api"; // API servisinin yolunu projenize göre ayarlayın

function EventDetailPage({ match }) {
  const [event, setEvent] = useState(null);
  const eventId = match.params.eventId;

  useEffect(() => {
    // Belirli etkinliği API'den çekmek için fetchEvent işlevini kullan
    fetchEventById(eventId)
      .then((data) => {
        setEvent(data);
      })
      .catch((error) => {
        console.error("Etkinlik alınamadı:", error);
      });
  }, [eventId]);

  if (!event) {
    return <div>Loading...</div>;
  }

  const groupStandsByGroup = (stands) => {
    const groupedStands = {};
    stands.forEach((stand) => {
      const group = stand.group;
      if (!groupedStands[group]) {
        groupedStands[group] = [];
      }
      groupedStands[group].push(stand);
    });
    return groupedStands;
  };

  // Gruplanmış standları state içinde tutacak bir state
  const [groupedStands, setGroupedStands] = useState({});

  useEffect(() => {
    // Belirli etkinliği API'den çekmek için fetchEventById işlevini kullan
    fetchEventById(eventId)
      .then((data) => {
        setEvent(data);
        // Standları grupla ve groupedStands state'ine ata
        const grouped = groupStandsByGroup(data.Stands);
        setGroupedStands(grouped);
      })
      .catch((error) => {
        console.error("Etkinlik alınamadı:", error);
      });
  }, [eventId]);

  // ... diğer kodlar ...

  return (
    <Container>
      {/* ... diğer JSX kodları ... */}
      <Row>
        <Col>
          <h2>Standlar</h2>
          {Object.keys(groupedStands).map((group) => (
            <div key={group}>
              <h3>{group}</h3>
              <table className="table">
                <thead>
                  <tr>
                    <th>Koltuk Numarası</th>
                    <th>Fiyat</th>
                  </tr>
                </thead>
                <tbody>
                  {groupedStands[group].map((stand) => (
                    <tr key={stand.id}>
                      <td>{stand.seatNumber}</td>
                      <td>{stand.price} TL</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </Col>
      </Row>
    </Container>
  );
}

export default EventDetailPage;
