import React, { useEffect, useState } from "react";
import { Card, Container, Row, Col } from "react-bootstrap";
import { fetchEvents } from "../services/api";

function HomePage() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Tüm etkinlikleri API'den al
    const fetchAllEvents = async () => {
      try {
        const eventData = await fetchEvents(true);
        setEvents(eventData);
      } catch (error) {
        console.error("Etkinlikler alınamadı:", error);
      }
    };

    fetchAllEvents();
  }, []);

  return (
    <Container>
      <h1>Tüm Etkinlikler</h1>
      <Row>
        {events.map((event) => (
          <Col key={event.id} md={4}>
            <Card>
              <Card.Img
                variant="top"
                src={
                  event.Photos[0]
                    ? `http://localhost:3000/api/uploads/${event.Photos[0].id}.jpg`
                    : `http://localhost:3000/api/uploads/sample.jpg`
                }
                alt={event.name}
                style={{ height: "200px" }}
              />
              <Card.Body style={{ maxHeight: "300px" }}>
                <Card.Title>{event.name}</Card.Title>
                <Card.Text>{event.description}</Card.Text>
                <Card.Text>Kategori: {event.categoryId}</Card.Text>
                <Card.Text>Adres: {event.address}</Card.Text>
                <Card.Text>Konum: {event.location}</Card.Text>
                <Card.Text>
                  Başlangıç Tarihi: {new Date(event.startedAt).toLocaleString()}
                </Card.Text>
                <Card.Text>
                  Bitiş Tarihi: {new Date(event.finishedAt).toLocaleString()}
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default HomePage;
