import React, { useEffect, useState } from "react";
import { fetchEvents } from "../services/api";

function ArchivedPage() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Tüm etkinlikleri API'den al
    const fetchAllEvents = async () => {
      try {
        const eventData = await fetchEvents();
        setEvents(eventData);
      } catch (error) {
        console.error("Etkinlikler alınamadı:", error);
      }
    };

    fetchAllEvents();
  }, []);

  return (
    <div>
      <h1>Tüm Etkinlikler</h1>
      <div className="event-list">
        {events.map((event) => (
          <div key={event.id} className="event">
            <h2>{event.name}</h2>
            <p>{event.description}</p>
            <p>Kategori: {event.categoryId}</p>
            <p>Adres: {event.address}</p>
            <p>Konum: {event.location}</p>
            <p>
              Başlangıç Tarihi: {new Date(event.startedAt).toLocaleString()}
            </p>
            <p>Bitiş Tarihi: {new Date(event.finishedAt).toLocaleString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ArchivedPage;
