import React, { useEffect, useState } from "react";
import { Card, Container, Row, Col, Pagination } from "react-bootstrap";
import { fetchEvents } from "../services/api";

function HomePage() {
  const [events, setEvents] = useState([]);
  const [page, setPage] = useState(1); // Şu anki sayfa numarası
  const [pageSize, setPageSize] = useState(10); // Sayfa başına gösterilen öğe sayısı
  const [totalPages, setTotalPages] = useState(1); // Toplam sayfa sayısı

  useEffect(() => {
    // Etkinlikleri API'den al
    const fetchAllEvents = async () => {
      try {
        const eventData = await fetchEvents({ page, pageSize });
        setEvents(eventData.rows); // Verileri alırken "rows" kullanın
        setTotalPages(Math.ceil(eventData.count / pageSize)); // Toplam sayfa sayısını hesapla
      } catch (error) {
        console.error("Etkinlikler alınamadı:", error);
      }
    };

    fetchAllEvents();
  }, [page, pageSize]);

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setPage(newPage);
    }
  };

  return (
    <Container>
      <h1 className="mt-4">Geçmiş Etkinlikler</h1>
      <Row>
        {events.map((event) => (
          <Col key={event.id} md={3}>
            <Card>
              <Card.Img
                variant="top"
                src={
                  event.Photos[0]
                    ? `http://localhost:3000/api/uploads/${event.Photos[0].id}.jpg`
                    : `http://localhost:3000/api/uploads/sample.jpg`
                }
                alt={event.name}
                style={{ height: "200px" }}
              />
              <Card.Body>
                <Row>
                  <Col>
                    <Row>
                      <Col md={7}>
                        <Card.Title className="mb-3">{event.name}</Card.Title>
                      </Col>
                      <Col md={5}>
                        {new Date(event.finishedAt).toLocaleDateString(
                          "tr-TR",
                          {
                            year: "numeric",
                            month: "long",
                            day: "numeric",
                          }
                        )}
                      </Col>
                    </Row>
                    <div className="border-top my-3"></div>{" "}
                    <Card.Subtitle className="mt-1 mb-2">
                      Açıklama
                    </Card.Subtitle>
                    <div className="border-top my-3"></div>{" "}
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <Pagination>
        <Pagination.Prev
          onClick={() => handlePageChange(page - 1)}
          disabled={page === 1}
        />
        {[...Array(totalPages).keys()].map((pageNumber) => (
          <Pagination.Item
            key={pageNumber + 1}
            active={pageNumber + 1 === page}
            onClick={() => handlePageChange(pageNumber + 1)}
          >
            {pageNumber + 1}
          </Pagination.Item>
        ))}
        <Pagination.Next
          onClick={() => handlePageChange(page + 1)}
          disabled={page === totalPages}
        />
      </Pagination>
    </Container>
  );
}

export default HomePage;
