import React, { useEffect, useState } from "react";
import { Card, Container, Row, Col } from "react-bootstrap";
import { fetchEvents } from "../services/api";

function HomePage() {
  const [events, setEvents] = useState([]);
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(10); // Sayfa boyutunu ayarlayabilirsiniz

  useEffect(() => {
    // Etkinlikleri API'den al
    const fetchEventsByPage = async () => {
      try {
        const eventData = await fetchEvents({ page, pageSize });
        setEvents(eventData.rows);
      } catch (error) {
        console.error("Etkinlikler alınamadı:", error);
      }
    };

    fetchEventsByPage();
  }, [page, pageSize]);

  // Sayfa numarasını artırma veya azaltma işlevleri ekleyebilirsiniz
  const nextPage = () => {
    setPage(page + 1);
  };

  const prevPage = () => {
    if (page > 1) {
      setPage(page - 1);
    }
  };

  return (
    <Container>
      <h1 className="mt-4">Geçmiş Etkinlikler</h1>
      <Row>
        {events.map((event) => (
          <Col key={event.id} md={3}>
            <Card>
              <Card.Img
                variant="top"
                src={
                  event.Photos[0]
                    ? `http://localhost:3000/api/uploads/${event.Photos[0].id}.jpg`
                    : `http://localhost:3000/api/uploads/sample.jpg`
                }
                alt={event.name}
                style={{ height: "200px" }}
              />
              <Card.Body>
                <Row>
                  <Col>
                    <Row>
                      <Col md={7}>
                        <Card.Title className="mb-3">{event.name}</Card.Title>
                      </Col>
                      <Col md={5}>
                        {new Date(event.finishedAt).toLocaleDateString(
                          "tr-TR",
                          {
                            year: "numeric",
                            month: "long",
                            day: "numeric",
                          }
                        )}
                      </Col>
                    </Row>
                    <div className="border-top my-3"></div>{" "}
                    <Card.Subtitle className="mt-1 mb-2">
                      Açıklama
                    </Card.Subtitle>
                    <div className="border-top my-3"></div>{" "}
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <div className="mt-4">
        <button onClick={prevPage}>Önceki Sayfa</button>
        <button onClick={nextPage}>Sonraki Sayfa</button>
      </div>
    </Container>
  );
}

export default HomePage;
