// src/pages/HomePage.js
import React, { useState, useEffect } from "react";
import { fetchEvents } from "../services/api";

function HomePage() {
  const [events, setEvents] = useState([]);
  const [filter, setFilter] = useState({
    date: "",
    type: "",
    place: "",
    city: "",
  });

  useEffect(() => {
    loadEvents();
  }, []);

  const loadEvents = async () => {
    try {
      const eventData = await fetchEvents(filter);
      setEvents(eventData);
    } catch (error) {
      console.error("Etkinlikler alınamadı:", error);
    }
  };

  const handleFilterChange = (e) => {
    setFilter({
      ...filter,
      [e.target.name]: e.target.value,
    });
  };

  const applyFilters = () => {
    loadEvents();
  };

  return (
    <div>
      <div className="filters">
        <input
          type="date"
          name="date"
          value={filter.date}
          onChange={handleFilterChange}
          placeholder="Tarih"
        />
        <input
          type="text"
          name="type"
          value={filter.type}
          onChange={handleFilterChange}
          placeholder="Etkinlik Türü"
        />
        <input
          type="text"
          name="place"
          value={filter.place}
          onChange={handleFilterChange}
          placeholder="Mekan"
        />
        <input
          type="text"
          name="city"
          value={filter.city}
          onChange={handleFilterChange}
          placeholder="Şehir"
        />
        <button onClick={applyFilters}>Filtrele</button>
      </div>
      <div className="event-list">
        {events.map((event) => (
          <div key={event.id} className="event">
            <h2>{event.name}</h2>
            <p>{event.description}</p>
            {/* Diğer etkinlik detayları... */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
