import React, { useEffect, useState } from "react";
import { Card, Container, Row, Col } from "react-bootstrap";
import { fetchEvents } from "../services/api";

function HomePage() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Tüm etkinlikleri API'den al
    const fetchAllEvents = async () => {
      try {
        const eventData = await fetchEvents({});
        setEvents(eventData);
      } catch (error) {
        console.error("Etkinlikler alınamadı:", error);
      }
    };

    fetchAllEvents();
  }, []);

  return (
    <Container>
      <h1>Geçmiş Etkinlikler</h1>
      <Row>
        {events.map((event) => (
          <Col key={event.id} md={3}>
            <Card>
              <Card.Img
                variant="top"
                src={
                  event.Photos[0]
                    ? `http://localhost:3000/api/uploads/${event.Photos[0].id}.jpg`
                    : `http://localhost:3000/api/uploads/sample.jpg`
                }
                alt={event.name}
                style={{ height: "200px" }}
              />
              <Card.Body>
                <Row>
                  <Col>
                    <Row>
                      <Col md={8}>
                        <Card.Title className="mb-3">{event.name}</Card.Title>
                      </Col>
                      <Col md={4}>
                        {new Date(event.finishedAt).toLocaleDateString(
                          "tr-TR",
                          {
                            year: "numeric",
                            month: "long",
                            day: "numeric",
                            hour: "2-digit",
                            minute: "2-digit",
                          }
                        )}
                      </Col>
                    </Row>
                    <div className="border-top my-3"></div>{" "}
                    <Card.Subtitle className="mt-1 mb-2">
                      Açıklama
                    </Card.Subtitle>
                    <Card.Text>{event.description}</Card.Text>
                    <Card.Subtitle className="mt-1 mb-2">Adres</Card.Subtitle>
                    <Row>
                      <Col md={9}>
                        <Card.Text>{event.address}</Card.Text>
                      </Col>
                      <Col md={3}>
                        <Card.Text>{event.city}</Card.Text>
                      </Col>
                    </Row>
                    <div className="border-top my-3"></div>{" "}
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default HomePage;
